def coverage(options):
    return """
# This is a managed file generated by Projen. To edit, change the project template.
# .coveragerc
[paths]
source =
    "{name}/"
    "*/site-packages"
[run]
branch = true
[report]
fail_under = {coverage}
show_missing = true
omit = 
    tests/
    {name}/cli/cli.py
    */__init__.py
  """.format(
        name=options["name"].replace("-", "_"),
        coverage=options.get("coverage", 70)
    )
